mapper.select:
SELECT NUM, "NAME", TITLE, CONTENT, PRICE, PLACE, ADDRESS, HEADNUM, HEADCOUNT, OPENDATE, CLOSEDATE, STARTTIME, ENDTIME, RECRUITDATE, RECRUITCLOSEDATE, REGDATE, UPDATEDATE FROM ( SELECT NUM, "NAME", TITLE, CONTENT, PRICE, PLACE, ADDRESS, HEADNUM, HEADCOUNT, OPENDATE, CLOSEDATE, STARTTIME, ENDTIME, RECRUITDATE, RECRUITCLOSEDATE, REGDATE, UPDATEDATE FROM T_CLASS
TITLE LIKE '%'||#{keyword}||'%' OR "NAME" LIKE '%'||#{keyword}||'%' OR CONTENT LIKE '%'||#{keyword}||'%'
ORDER BY OPENDATE DESC ,CLOSEDATE
) T
WHERE ROWNUM <= #{pageNum} * #{amount} AND ROWNUM > (#{pageNum} - 1) * #{amount}

mapper.update:
UPDATE T_CLASS
SET "NAME" = #{name}, TITLE = #{title}, CONTENT = #{content}, PRICE = #{price}, HEADNUM = #{headNum}, OPENDATE = #{openDate}, CLOSEDATE = #{closeDate}, STARTTIME = #{startTime},
    ENDTIME = #{endTime}, RECRUITDATE = #{recruitDate}, RECRUITCLOSEDATE = #{recruitCloseDate}, UPDATEDATE = SYSDATE, PLACE = #{place}, ADDRESS = #{address}
WHERE NUM = #{num}

UPDATE T_CLASS
SET HEADCOUNT = HEADCOUNT + 1
WHERE NUM = #{num}

mapper.select:
SELECT COUNT(NUM) FROM (SELECT NUM FROM T_CLASS
TITLE LIKE '%'||#{keyword}||'%' OR "NAME" LIKE '%'||#{keyword}||'%' OR CONTENT LIKE '%'||#{keyword}||'%'
)
